from typing import List


class Solution:
    def threeSum(self, nums: List[int]):
        arr = []
        for i in range(len(nums) -1):
            s = set()
            for j in range(i+1, len(nums)):
                val = -(nums[i] + nums[j])
                if val in s:
                    # arr.insert(x, [val, nums[i], nums[j]])
                    temp = [val, nums[i], nums[j]]
                    temp.sort()
                    if temp not in arr:
                        arr.append(temp)
                else:
                    s.add(nums[j])
        return arr

    def findTriplets(self, nums):

        found = False
        nums.sort()
        n = len(nums)
        arr_ = []
        for i in range(0, n - 1):
            l = i + 1
            r = n - 1
            x = nums[i]
            while (l < r):
                if (x + nums[l] + nums[r] == 0):
                    temp = [x, nums[l], nums[r]]
                    temp.sort()
                    if temp not in arr_:
                        arr_.append(temp)
                    l += 1
                    r -= 1
                    found = True
                elif (x + nums[l] + nums[r] < 0):
                    l += 1

                else:
                    r -= 1

        return arr_


